<script>
/ contact form /
$(document).ready(function(){
    $( ".body_blog .page-heading" ).replaceWith( "<h1 class='page-heading'>THE <span style='color:#ec008c;'>SC</span>OOP</h1>" );
	 $(window).on('load', function(e) {
		 if($('#nl_name').val() != '')
			 {
				$('#nl_name').prev('label').addClass('active'); 
			 }
		 if($('#nl_email').val() != '')
			 {
				$('#nl_email').prev('label').addClass('active'); 
			 }
		 if($('#nl_message').val() != '')
			 {
				$('#nl_message').prev('label').addClass('active'); 
			 }
		 });
    $('.form').find('input, textarea').on('keyup blur focus', function(e) {
      var $this = $(this),
        label = $this.prev('label');
      if (e.type === 'keyup') {
        if ($this.val() === '') {
          label.removeClass('active highlight');
        } else {
          label.addClass('active highlight');
        }
      } else if (e.type === 'blur') {
        if ($this.val() === '') {
          label.removeClass('active highlight');
        } else {
          label.removeClass('highlight');
        }
      } else if (e.type === 'focus') {

        if ($this.val() === '') {
          label.removeClass('highlight');
        } else if ($this.val() !== '') {
          label.addClass('highlight');
        }
      }
    });
});
</script>

<script>
$(document).ready(function(){
  $("#myInput").on("keyup", function() {
    var value = $(this).val().toLowerCase();
    $("#myList div").filter(function() {
      $(this).toggle($(this).text().toLowerCase().indexOf(value) > -1)
    });
  });
});
</script>

<script>
// Get the modal
var modal = document.getElementById("myModal");

// Get the button that opens the modal
var btn = document.getElementById("myBtn");

// Get the <span> element that closes the modal
var span = document.getElementsByClassName("close")[0];

// When the user clicks the button, open the modal 
btn.onclick = function() {
  modal.style.display = "block";
}

// When the user clicks on <span> (x), close the modal
span.onclick = function() {
  modal.style.display = "none";
}

// When the user clicks anywhere outside of the modal, close it
window.onclick = function(event) {
  if (event.target == modal) {
    modal.style.display = "none";
  }
}
</script>

