pages/custom/category/sitewide-banners:
{{#if banners.top}}
    <div class="banners" data-banner-location="top">
        <div class="banner">
            {{#each banners.top}}
                {{{this}}}
            {{/each}}
        </div>
    </div>
{{/if}}
{{#if banners.bottom}}
    <div class="banners" data-banner-location="bottom">
        <div class="banner">
            {{#each banners.bottom}}
                {{{this}}}
            {{/each}}
        </div>
    </div>
{{/if}}
