$(document).ready(function(){
    $( ".body_blog .page-heading" ).replaceWith( "<h1 class='page-heading'>THE <span style='color:#ec008c;'>SC</span>OOP</h1>" );
	 $(window).on('load', function(e) {
		 if($('#nl_name').val() != '')
			 {
				$('#nl_name').prev('label').addClass('active'); 
			 }
		 if($('#nl_email').val() != '')
			 {
				$('#nl_email').prev('label').addClass('active'); 
			 }
		 if($('#nl_message').val() != '')
			 {
				$('#nl_message').prev('label').addClass('active'); 
			 }
		 });
    $('.form').find('input, textarea').on('keyup blur focus', function(e) {
      var $this = $(this),
        label = $this.prev('label');
      if (e.type === 'keyup') {
        if ($this.val() === '') {
          label.removeClass('active highlight');
        } else {
          label.addClass('active highlight');
        }
      } else if (e.type === 'blur') {
        if ($this.val() === '') {
          label.removeClass('active highlight');
        } else {
          label.removeClass('highlight');
        }
      } else if (e.type === 'focus') {

        if ($this.val() === '') {
          label.removeClass('highlight');
        } else if ($this.val() !== '') {
          label.addClass('highlight');
        }
      }
    });
});
</script>

<script>
