components/faceted-search/selected-facet:
{{#if this.items}}
    <div class="facetedSearch-refineFilters sidebarBlock">
        <h5 class="sidebarBlock-heading">
            {{lang 'search.faceted.selected.title'}}
        </h5>
    
        
        <ul class="inlineList inlineList--labels">
            {{#each this.items}}
                <li>
                    <a
                        href="{{ url }}"
                        class="facetLabel"
                        rel="nofollow"
                        data-faceted-search-facet>
                        {{#if facet '===' 'rating'}}
                            {{lang 'search.faceted.selected.rating-label' rating=title}}
                        {{else}}
                            {{{ sanitize title }}}
                        {{/if}}
    
                        <svg class="icon">
                            <use xlink:href="#icon-close" />
                        </svg>
                    </a>
                </li>
            {{/each}}
        </ul>
    
        {{#if remove_all_url}}
            <a href="{{ remove_all_url }}" data-faceted-search-facet>
                {{lang 'search.faceted.selected.clear-all'}}
            </a>
        {{/if}}
    </div>
<script src="https://code.jquery.com/jquery-3.5.1.min.js"></script>
<script>
	
	$(function() {
    $('.accordion-navigation.toggleLink').removeClass('is-open');
			$('.accordion-navigation.toggleLink').attr('aria-expanded', 'false');
			$('.accordion-content').removeClass('is-open');
			$('.accordion-content').attr('aria-hidden', 'true');
});
	
</script>

{{/if}}
